// KRONOS Advanced Personalization Engine
// Uses rich Airtable data for hyper-personalized outreach

for (const item of $input.all()) {
  // Extract all available fields from Airtable
  const fullName = item.json['FULL NAME'] || 'there';
  const firstName = fullName.split(' ')[0] || 'there';
  const email = item.json.EMAIL || item.json.CONTACT;
  const company = item.json['company name'] || 'your company';
  const jobTitle = item.json['JOB TITLE'] || 'your role';
  const companyDesc = item.json['COMPANY DESCRIPTION'] || '';
  const companySize = item.json['COMPANY SIZE'] || '';
  const headline = item.json.HEADLINE || '';
  const seniority = item.json.SENIORITY || '';
  const category = item.json.Category || '';
  const sector = item.json.SECTOR || '';
  const revenue = item.json.REVENUE || '';
  const technology = item.json.TECHNOLOGY || '';
  const city = item.json.City || '';
  const state = item.json.State || '';
  const keywords = item.json.KEYWORDS || '';
  const linkedIn = item.json.LINKEDIN || '';
  const url = item.json.URL || '';

  // Smart subject line generation based on available data
  const subjectTemplates = [];

  if (technology) {
    subjectTemplates.push(`${firstName}, re: ${company}'s ${technology.split(',')[0].trim()} stack`);
  }

  if (sector) {
    subjectTemplates.push(`Helping ${sector} companies like ${company}`);
  }

  if (companySize) {
    subjectTemplates.push(`${firstName} - scaling ${category || 'your business'}`);
  }

  if (headline) {
    const headlineKeyword = headline.split(' ').slice(0, 3).join(' ');
    subjectTemplates.push(`Re: ${headlineKeyword}`);
  }

  // Fallback subject lines
  subjectTemplates.push(
    `Quick question about ${company}`,
    `${firstName}, thoughts on ${category || 'your business'}?`,
    `Partnership opportunity for ${company}`
  );

  const randomSubject = subjectTemplates[Math.floor(Math.random() * subjectTemplates.length)];

  // Intelligent email body personalization
  let emailBody = `Hi ${firstName},\n\n`;

  // Opening line based on available data
  if (headline && linkedIn) {
    emailBody += `I came across your profile on LinkedIn - "${headline}" - and was impressed by your work at ${company}.\n\n`;
  } else if (jobTitle && seniority) {
    emailBody += `I noticed you're a ${seniority} ${jobTitle} at ${company} and wanted to reach out directly.\n\n`;
  } else {
    emailBody += `I've been researching ${sector || category || 'companies'} in ${city || 'your area'} and ${company} stood out.\n\n`;
  }

  // Value proposition based on company data
  if (companyDesc) {
    const descSnippet = companyDesc.length > 100 ? companyDesc.substring(0, 100) + '...' : companyDesc;
    emailBody += `I saw that ${company} is ${descSnippet.toLowerCase()}\n\n`;
  }

  if (technology) {
    const techStack = technology.split(',').slice(0, 2).join(' and ');
    emailBody += `I noticed you're using ${techStack}. We've helped similar companies optimize their tech stack and [YOUR SPECIFIC VALUE PROP].\n\n`;
  } else if (revenue && companySize) {
    emailBody += `As a ${revenue} company with ${companySize} employees, I thought you might be interested in [YOUR VALUE PROP].\n\n`;
  } else {
    emailBody += `We specialize in helping ${category || sector || 'companies like yours'} with [YOUR SPECIFIC VALUE PROPOSITION].\n\n`;
  }

  // Call to action based on seniority
  if (seniority === 'C-Level') {
    emailBody += `Would you be open to a brief 15-minute call to discuss how we could help ${company} achieve [SPECIFIC OUTCOME]?\n\n`;
  } else if (seniority === 'VP' || seniority === 'Director') {
    emailBody += `I'd love to share some insights on how companies in ${sector || category || 'your industry'} are solving [SPECIFIC PROBLEM]. Would you have 10 minutes for a quick call?\n\n`;
  } else {
    emailBody += `Would you be the right person to discuss this, or should I connect with someone else on your team?\n\n`;
  }

  // Signature
  emailBody += `Best regards,\n[YOUR NAME]\n[YOUR TITLE]\n[YOUR COMPANY]\n[YOUR CONTACT INFO]`;

  // Add enriched fields to item
  item.json.personalizedSubject = randomSubject;
  item.json.personalizedBody = emailBody;
  item.json.email = email;
  item.json.fullName = fullName;
  item.json.firstName = firstName;
  item.json.outreachDate = new Date().toISOString();
  item.json.campaignId = 'KRONOS-001';
  item.json.status = 'sent';

  // Store original Airtable fields for logging
  item.json.company = company;
  item.json.jobTitle = jobTitle;
  item.json.sector = sector;
  item.json.technology = technology;
  item.json.revenue = revenue;
  item.json.linkedInUrl = linkedIn;
}

return $input.all();

// KRONOS Follow-up Logic with Intelligence
const now = new Date();
const followupItems = [];

for (const item of $input.all()) {
  const sentDate = new Date(item.json.sentDate);
  const daysSinceSent = Math.floor((now - sentDate) / (1000 * 60 * 60 * 24));

  // Extract contact info
  const firstName = item.json.fullName ? item.json.fullName.split(' ')[0] : 'there';
  const company = item.json.company || 'your company';
  const sector = item.json.sector || 'your industry';
  const technology = item.json.technology || '';

  // Send follow-up after 3 days if no response
  if (daysSinceSent >= 3 && daysSinceSent <= 4 && item.json.status === 'sent') {
    item.json.followupSubject = `Re: ${item.json.subject}`;

    // Intelligent follow-up body based on original data
    let followupBody = `Hi ${firstName},\n\n`;
    followupBody += `Following up on my email from earlier this week about ${company}.\n\n`;

    if (technology) {
      const tech = technology.split(',')[0].trim();
      followupBody += `I wanted to share a quick insight: We recently helped a similar company optimize their ${tech} implementation and saw [SPECIFIC RESULT].\n\n`;
    } else if (sector) {
      followupBody += `Given the challenges in the ${sector} space right now, I thought this might be particularly relevant for ${company}.\n\n`;
    } else {
      followupBody += `I have some specific ideas on how we could help ${company} achieve [SPECIFIC OUTCOME].\n\n`;
    }

    followupBody += `Would you have 10 minutes for a quick call this week?\n\n`;
    followupBody += `Best regards,\n[YOUR NAME]`;

    item.json.followupBody = followupBody;
    item.json.firstName = firstName;
    item.json.isFollowup = true;
    followupItems.push(item);
  }
}

return followupItems;
